import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import axios from 'axios'

const Wrapper = styled.div`
    max-width: 1024px;
    margin: 0 auto;
    width: 100%;
    height: 100%;
    background-color: hsl(209,23%,22%);
    display: flex;
    flex-wrap: wrap;
`

const DataInfoText = styled.p`
    color: #FFF;
    font-family: 'Nunito Sans';
    font-weight: 600;
    margin: 1em 0;
`

const DataInfoTitle = styled.h2`
    color: #FFF;
    font-family: 'Nunito Sans';
    font-weight: 600;
    margin: 1em 0;
    font-size: 24px;
`

const Input = styled.input`
    background-color: hsl(207,26%,17%);
    font-family: 'Nunito Sans';
    font-weight: 600;
    font-size: 16px;
    width: 40%;
    height: 50px;
    border: none;
    margin: 1em;
    outline: none;
    color: white;
    padding: 0 2em;

    &::placeholder {
        color: white;
    }
`

const Select = styled.select`
    background-color: hsl(207,26%,17%);
    font-family: 'Nunito Sans';
    font-weight: 600;
    font-size: 16px;
    width: 30%;
    height: 50px;
    border: none;
    margin: 1em;
    outline: none;
    color: white;
    padding: 0 1em;
    margin-left: auto;

    &::placeholder {
        color: white;
    }
`

const Option = styled.option`
    border: 0;
    background-color: hsl(207,26%,17%);
    font-family: 'Nunito Sans';
    font-weight: 600;
    font-size: 16px;
    outline: none;
`

interface CountryCard {
    flag: string
    name: string
    population: number
    region: string
    capital: string
  }


const CountrySearch: React.FC<CountryCard>  = ( { flag, name, population, region, capital } ) => { 

    return (   
                <div style={{ width: '23%', height: '450px', marginLeft: '1em'}}>
                    <div style={{ backgroundImage: `url(${flag})`, width: '100%', height: '50%', backgroundSize: 'cover', backgroundPosition: 'center', borderTopLeftRadius: '10px', borderTopRightRadius: '10px'}}></div>
                    <DataInfoTitle>{name}</DataInfoTitle>
                    <DataInfoText>Population: {population}</DataInfoText>
                    <DataInfoText>Region: {region}</DataInfoText>
                    <DataInfoText>Capital: {capital}</DataInfoText>
                </div> 
    )
}


export default CountrySearch
