import React, {useEffect, useState} from 'react'
import axios from 'axios'
import styled from 'styled-components'


const Wrapper = styled.div`
    max-width: 1024px;
    margin: 0 auto;
    width: 100%;
    height: 100%;
    background-color: hsl(209,23%,22%);
    display: flex;
    flex-wrap: wrap;
`

const DataInfoText = styled.p`
    color: #FFF;
    font-family: 'Nunito Sans';
    font-weight: 600;
    margin: 1em 0;
`

const DataInfoTitle = styled.h2`
    color: #FFF;
    font-family: 'Nunito Sans';
    font-weight: 600;
    margin: 1em 0;
    font-size: 24px;
`

const Input = styled.input`
    background-color: hsl(207,26%,17%);
    font-family: 'Nunito Sans';
    font-weight: 600;
    font-size: 16px;
    width: 50%;
    height: 50px;
    border: none;
    margin: 1em;
    outline: none;
    color: white;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    padding: 0 2em;

    &::placeholder {
        color: white;
    }
`


interface CountryCard {
    flag: string
    name: string
    population: number
    region: string
    capital: string
}


const CountryCard = () => {
    const [drzave, setDrzave] = useState<Array<CountryCard>>([{
        flag: "",
        name: "",
        population: 0,
        region: "",
        capital: ""
    }])



    useEffect(() => {
        axios.get('https://restcountries.eu/rest/v2/all')
             .then((res) => {
                 setDrzave(res.data) 
             })
    },[])


    console.log(drzave)

    return (
        <Wrapper>
            <Input placeholder="âŒ•   Search for a country" />
            <Wrapper>
            {drzave.map( drzava => <div style={{ width: '23%', height: '450px', marginLeft: '1em'}} key={drzava.name}>
                <div style={{ backgroundImage: `url(${drzava.flag})`, width: '100%', height: '50%', backgroundSize: 'cover', backgroundPosition: 'center', borderTopLeftRadius: '10px', borderTopRightRadius: '10px'}}></div>
                <DataInfoTitle>{drzava.name}</DataInfoTitle>
                <DataInfoText>Population: {drzava.population}</DataInfoText>
                <DataInfoText>Region: {drzava.region}</DataInfoText>
                <DataInfoText>Capital: {drzava.capital}</DataInfoText>
            </div> )}
            </Wrapper>
        </Wrapper>
    )
}


export default CountryCard
