import React, {useState, useEffect} from 'react';
import axios from 'axios'
import styled from 'styled-components'

import Header from './components/Header'
import CountrySearch from './components/CountrySearch'



const Input = styled.input`
    background-color: hsl(207,26%,17%);
    font-family: 'Nunito Sans';
    font-weight: 600;
    font-size: 16px;
    width: 50%;
    height: 50px;
    border: none;
    margin: 1em;
    outline: none;
    color: white;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    padding: 0 2em;

    &::placeholder {
        color: white;
    }
`

const Wrapper = styled.div`
    max-width: 1024px;
    margin: 0 auto;
    width: 100%;
    height: 100%;
    background-color: hsl(209,23%,22%);
    display: flex;
    flex-wrap: wrap;
`

const App:React.FC = () => {

  const [drzave, setDrzave] = useState<Array<{}>>([])

  const [q,setQ] = useState<string>("")


  useEffect(() => {
    axios.get('https://restcountries.eu/rest/v2/all')
         .then((res) => {
             setDrzave(res.data) 
         })
  },[])


  const Search = (country: any) => {
    return country.filter((f: any) => f.name.toLowerCase().indexOf(q) > -1)

  }

  return (
    <Wrapper>
      <Header />
      <Input placeholder="âŒ•   Search for a country" value={q} onChange={(e:any) => setQ(e.target.value )}/>
      <CountrySearch drzave={Search(drzave)} />
    </Wrapper>
  )
}

export default App;
